#!/usr/bin/env pythonimport sysimport os, time, pdbimport Sensorclass Rate (Sensor.Connection):	def __init__(self,ts):		self.sensor = Sensor.Connection()		self.time_span = ts*3600	def getRate (self):				query = """select tnf.name from_node, tnt.name to_node, sum(hle.done_bytes)/1048576/:time_span rate                            from t_history_link_events hle 				join t_adm_node tnf on tnf.id = hle.from_node				join t_adm_node tnt on tnt.id = hle.to_node			   where hle.timebin >= (sysdate - TO_DATE('01.01.1970:00:00:00','DD.MM.YYYY:HH24:MI:SS'))*24*60*60 - :time_span			   group by (tnf.name, tnt.name)"""		self.sensor.cursor.execute (query,{"time_span":self.time_span})		table = self.sensor.cursor.fetchall()				for row in table:			link [0] = row[0]			link [1] = row[1]			rate = row[2]			 			table_rate [link] = rate				return table_rate